  handleSaveClick() {
    if (this.username == '' || this.email == '' || this.creationDate == '') {
      alert('Please fill in all sections.');
    } else if (
      this.userService.checkUnique(this.username, this.email, this.userId) ===
      false
    ) {
      alert('Username and email must be unique.');
    } else {
      const user: User = {
        userId: this.userId,
        username: this.username,
        email: this.email,
        creationDate: this.creationDate,
        isActive: this.isActive,
      };
      this.userService.editUser(user, this.userId);
      this.users = this.userService.getUsers();
      this.handleCancelClick();
    }
  }

  //userId: Number(this.users[this.users.length-1].userId) + 1,

  handleEditClick($event: number): void {
    this.editMode = true;
    this.userId = $event;
  }

  handleCancelClick(): void {
    this.editMode = false;
    this.username = '';
    this.email = '';
    this.creationDate = '';
    this.userId = 0;
  }
}
