import { Component } from '@angular/core';
import { Comment } from '../comment';
import { CommentService } from '../comment.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comment-detail',
  templateUrl: './comment-detail.component.html',
  styleUrls: ['./comment-detail.component.css']
})
export class CommentDetailComponent {
  comment: Comment = {
    commentId: 0,
    postId: 0,
    userId: 0,
    comment: "",
    creationDate: "",
    isConfirmed: false
  };

  constructor(private commentService: CommentService, private activatedRoute: ActivatedRoute){

  }


  ngOnInit() {
    this.activatedRoute.params.subscribe(params => {
      this.comm = this.postService.getPosts();
      const id = params['id'];
      this.post = this.posts.find(post => post.postId === Number(id))!;
    });
  }

}
<div class="comment-card">
  <div class="comment">
    <div class="header">
      <p>Comment Id: {{ comment.commentId }}</p>
      <p>Post Id: {{ comment.postId | uppercase }}</p>
      <p>User Id: {{ comment.userId }}</p>
    </div>
    <div class="content">
      <h2>Content</h2>
      <p>{{ comment.comment }}</p>
    </div>
    <div class="footer">
      <div>
        <h2>Creation Date</h2>
        <p>{{ comment.creationDate }}</p>
        <h2>Confirmation State</h2>
        <p>{{ comment.isConfirmed }}</p>
      </div>
    </div>
  </div>
</div>
