import { Component } from '@angular/core';
import { PostService } from '../post.service';
import { Post } from '../post';
import { Router } from '@angular/router';

@Component({
  selector: 'app-post-list',
  templateUrl: './post-list.component.html',
  styleUrls: ['./post-list.component.css'],
})
export class PostListComponent {
  posts: Post[] = [];
  constructor(private postService: PostService, private router: Router) {
    this.postService.setPosts();
    this.posts = this.postService.getPosts();
  }

  handleDeleteClick($event: number): void {
    this.postService.deletePost($event);
    this.posts = this.postService.getPosts();
  }

  handleDetailClick($event: number): void {
    this.router.navigate(['/postlist/', $event]);
  }
}
<table>
    <thead>
    <tr>
        <th>Post Id</th>
        <th>Title</th>
        <th>View Count</th>
        <th>Creation Date</th>
        <th>Publication State</th>
    </tr>
</thead>
<tbody>
        <tr *ngFor="let item of posts">
            <td>{{item.postId}}</td>
            <td>{{item.title}}</td>
            <td>{{item.viewCount}}</td>
            <td>{{item.creationDate}}</td>
           <td>{{item.isPublished}}</td>
           <app-button [buttonText]="'Detail'" [id]="item.postId"
           (onButtonClick)="handleDetailClick($event)"></app-button>
           <app-button [buttonText]="'Delete'" [id]="item.postId"
           (onButtonClick)="handleDeleteClick($event)"></app-button>

        </tr>
</tbody>
</table>
<button [routerLink]="['/routePath']" routerLinkActive="router-link-active" >Add Post<button>
