import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { User } from '../user';
import {FormsModule} from '@angular/forms';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css']
})
export class UserListComponent {
  users: User[] = [];
constructor(private userService : UserService) {
  this.userService.setUsers();
this.users = this.userService.getUsers();

}
handleDeleteClick($event: Number){
this.userService.deleteUser($event);
this.users = this.userService.getUsers();
}
}
<table class="table-container">
  <thead>
    <tr>
      <th>USERID</th>
      <th>USERNAME</th>
      <th>EMAIL</th>
      <th>CREATION DATE</th>
      <th>ISACTIVE</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of users">
      <td>{{ item.userId }}</td>
      <td>{{ item.username }}</td>
      <td>{{ item.email }}</td>
      <td>{{ item.creationDate | date}}</td>
      <td>{{ item.isActive }}</td>
      <td>
        <app-button [buttonText] = "'Delete'" [id] = "item.userId" (onButtonClick) = "handleDeleteClick($event)">
        </app-button>

      </td>
    </tr>
  </tbody>
</table>

<form action="">
  <label for="username">Username</label>
  <input type="text" [(ngModel)]="username" name="username">
  <label for="email">Email</label>
  <input type="email" [(ngModel)]="email" name="email">
  <label for="creationDate">Creation Date</label>
  <input type="date" [(ngModel)]="creationDate" name="creationDate">
  <label for="creationDate">User Activation State</label>
  <select [(ngModel)]="isActive" name="isActive" >
    <option *ngFor="let item of list" [value]="item">Active</option>
    <option value="false">False</option>
  </select>
</form>
