import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { User } from '../user';
import { PostService } from 'src/app/posts/post.service';
import { CommentService } from 'src/app/comments/comment.service';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css'],
})
export class UserListComponent {
  users: User[] = [];
  pagedData: User[] = [];
  currentPage = 1;
  itemsPerPage = 10;

  //forms kisminda yer alan degiskenler tanimlandi.
  username: string = '';
  email: string = '';
  creationDate: string = '';
  isActive: boolean = false;
  editMode: boolean = false;
  userId: number = 0;

  constructor(
    private userService: UserService,
    private postService: PostService,
    private commentService: CommentService
  ) {
    if (this.userService.getUsers().length === 0)
      this.userService.setUsers();
    this.users = this.userService.getUsers();
    if (this.postService.getPosts().length === 0)
      this.postService.setPosts();
    if (this.commentService.getComments().length === 0)
      this.commentService.setComments();
  }

  //son kullanici kalmasi durumu engellendi.
  handleDeleteClick($event: number) {
    if (this.userService.userCount() === 1) {
      alert('You cannot delete the last user.');
    } else if(this.checkPostsAndComments($event) === true)
    alert("You cannot delete users with posts or comments.");
    else {
      this.userService.deleteUser($event);
      this.users = this.userService.getUsers();
    }
  }

  checkPostsAndComments(id: number): boolean {
    if (this.postService.getPosts().filter((post) => post.userId === id).length !== 0)
      return true;
    else if (this.commentService.getComments().filter((comment) => comment.userId === id).length !== 0)
      return true;
    else
      return false;
  }


  handleSaveClick() {
    if (this.username == '' || this.email == '' || this.creationDate == '') {
      alert('Please fill in all sections.');
    } else if (
      this.userService.checkUnique(this.username, this.email, this.userId) ===
      false
    ) {
      alert('Username and email must be unique.');
    } else {
      const user: User = {
        userId: this.userId,
        username: this.username,
        email: this.email,
        creationDate: this.creationDate,
        isActive: this.isActive,
      };
      this.userService.editUser(user, this.userId);
      this.users = this.userService.getUsers();
      this.handleCancelClick();
    }
  }



  handleEditClick($event: number): void {
    this.editMode = true;
    this.userId = $event;
  }

  handleCancelClick(): void {
    this.editMode = false;
    this.username = '';
    this.email = '';
    this.creationDate = '';
    this.userId = 0;
  }


  ngOnInit() {
    this.pageChanged(this.currentPage);
  }

  pageChanged(page: number): void {
    const startIndex = (page - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    this.pagedData = this.users.slice(startIndex, endIndex);
    this.currentPage = page;
    if (this.pagedData.length === 0 && this.currentPage > 1) {
      this.previousPage();
    }
  }

  previousPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.pageChanged(this.currentPage);
    }
  }

  nextPage(): void {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.pageChanged(this.currentPage);
    }
  }

  get totalPages(): number {
    return Math.ceil(this.users.length / this.itemsPerPage);
  }

}
<table class="table-container">
  <thead class="table-head">
    <tr>
      <th>USER ID</th>
      <th>USERNAME</th>
      <th>EMAIL</th>
      <th>CREATION DATE</th>
      <th>IS ACTIVE</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of pagedData">
      <td>{{ item.userId }}</td>
      <td>{{ item.username }}</td>
      <td>{{ item.email }}</td>
      <td>{{ item.creationDate | date }}</td>
      <td>{{ item.isActive }}</td>
      <td class="button">
        <app-button
          [buttonText]="'Delete'"
          [id]="item.userId"
          (onButtonClick)="handleDeleteClick($event)"
      >
        </app-button>
        <app-button
          [buttonText]="'Edit'"
          [id]="item.userId"
          (onButtonClick)="handleEditClick($event)"
        ></app-button>
      </td>
    </tr>
  </tbody>
</table> <br>


<div class="pagination" *ngIf="totalPages > 0">
  <button [disabled]="currentPage === 1" (click)="previousPage()">Previous</button>
  <span>{{currentPage}} </span>
  <button [disabled]="currentPage === totalPages" (click)="nextPage()">Next</button>
</div>

<a routerLink="/useradd"><button>Add User</button></a>

<div class="edit-section">
<form *ngIf="editMode">
  <label for="username">Username</label>
  <input type="text" [(ngModel)]="username" name="username" />
  <label for="email">Email</label>
  <input type="email" [(ngModel)]="email" name="email" />
  <label for="creationDate">Creation Date</label>
  <input type="date" [(ngModel)]="creationDate" name="creationDate" />
  <label for="isActive">Activation State</label>
  <select [(ngModel)]="isActive" name="isActive">
    <option [value]="true">Active</option>
    <option [value]="false">Inactive</option>
  </select>

  <div>
    <button (click)="handleSaveClick()" type="button">Save</button>
    <button (click)="handleCancelClick()" type="button">Cancel</button>
  </div>

</form>

</div>
