import { Component } from '@angular/core';
import { PostService } from '../post.service';
import { Post } from '../post';
import { Router } from '@angular/router';

@Component({
  selector: 'app-post-list',
  templateUrl: './post-list.component.html',
  styleUrls: ['./post-list.component.css'],
})
export class PostListComponent {
  posts: Post[] = [];
  pagedData: Post[] = [];
  currentPage = 1;
  itemsPerPage = 10;

  constructor(private postService: PostService, private router: Router) {
    if (this.postService.getPosts().length === 0) {
      this.postService.setPosts();
    }
    this.posts = this.postService.getPosts();
  }

  handleDeleteClick($event: number): void {
    this.postService.deletePost($event);
    this.posts = this.postService.getPosts();
    this.pageChanged(this.currentPage);
  }

  handleDetailClick($event: number): void {
    this.router.navigate(['/postlist/', $event]);
  }

  ngOnInit(){
    this.pageChanged(this.currentPage);
  }

  pageChanged(page: number): void {
    const startIndex = (page - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    this.pagedData = this.posts.slice(startIndex, endIndex);
    this.currentPage = page;
    if(this.pagedData.length === 0 && this.currentPage > 1){
      this.previousPage();
    }
  }

  previousPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.pageChanged(this.currentPage);
    }
  }

  nextPage(): void {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.pageChanged(this.currentPage);
    }
  }

  get totalPages(): number {
    return Math.ceil(this.posts.length / this.itemsPerPage);
  }
}
<table>
    <thead>
    <tr>
        <th>POST ID</th>
        <th>TITLE</th>
        <th>VIEW COUNT</th>
        <th>CREATION DATE</th>
        <th>PUBLICATION STATE</th>
    </tr>
</thead>
<tbody>
        <tr *ngFor="let item of pagedData">
            <td>{{item.postId}}</td>
            <td>{{item.title}}</td>
            <td>{{item.viewCount}}</td>
            <td>{{item.creationDate}}</td>
           <td>{{item.isPublished}}</td>
           <td class="button">
           <app-button [buttonText]="'Detail'" [id]="item.postId"
           (onButtonClick)="handleDetailClick($event)"></app-button>
           <app-button [buttonText]="'Delete'" [id]="item.postId"
           (onButtonClick)="handleDeleteClick($event)"></app-button></td>
        </tr>
</tbody>
</table>

<div class="pagination" *ngIf="totalPages > 0">
    <button [disabled]="currentPage === 1" (click)="previousPage()">Previous</button>
    <span>{{currentPage}} </span>
    <button [disabled]="currentPage === totalPages" (click)="nextPage()">Next</button>
</div>

<button [routerLink]="'/postadd'">Add Post<button>
