import { Component } from '@angular/core';
import { Post } from '../post';
import { PostService } from '../post.service';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from 'src/app/user/user.service';
import { User } from 'src/app/user/user';
import { Category } from 'src/app/category/category';
import { CategoryService } from 'src/app/category/category.service';

@Component({
  selector: 'app-post-detail',
  templateUrl: './post-detail.component.html',
  styleUrls: ['./post-detail.component.css'],
})
export class PostDetailComponent {
  posts: Post[] = [];
  post: Post = {
    postId: 0,
    userId: 0,
    categoryId: 0,
    title: '',
    content: '',
    viewCount: 0,
    creationDate: '',
    isPublished: false,
  };

  users: User[] = [];
  categories: Category[] = [];
  editMode: Boolean = false;

  constructor(
    private postService: PostService,
    private router: Router,
    private activatedRoute: ActivatedRoute,
    private userService: UserService,
    private categoryService: CategoryService
  ) {
    if (this.userService.getUsers().length === 0) this.userService.setUsers();
    else this.users = this.userService.getUsers();
    if (this.categoryService.getCategories().length === 0)
      this.categoryService.setCategories();
    else this.categories = this.categoryService.getCategories();
  }

  ngOnInit() {
    this.activatedRoute.params.subscribe((params) => {
      this.posts = this.postService.getPosts();
      const id = params['id'];
      this.post = this.posts.find((post) => post.postId === Number(id))!;
    });
  }

  handleSaveClick() {
    this.postService.updatePost(this.post);
    this.router.navigateByUrl('/postlist');
  }

  handleDeleteClick() {
    this.postService.deletePost(this.post.postId);
    this.router.navigateByUrl('/postlist');
  }

  handleEditClick() {
    this.editMode = !this.editMode;
  }
}
<div class="post-detail-component">
  <div class="post-card">
    <div class="post-detail">
      <div class="header">
        <p>Post Id: {{ post.postId }}</p>
        <h1>Title: {{ post.title | uppercase }}</h1>
      </div>
      <div class="content">
        <h2>Post Content</h2>
        <p>{{ post.content }}</p>
      </div>
      <div class="footer">
        <div>
          <h2>Category</h2>
          <p>{{ categories[post.categoryId - 1].name }}</p>
        </div>
        <div>
          <h2>Date</h2>
          <p>{{ post.creationDate }}</p>
        </div>
        <div>
          <h2>Author</h2>
          <p>
            Username: {{ users[post.userId - 1].username }} User Id:
            {{ post.userId }}
          </p>
          <p></p>
        </div>
        <div>
          <h2>View Count</h2>
          <p>{{ post.viewCount }}</p>
        </div>
        <div>
          <h2>Is Published</h2>
          <p>{{ post.isPublished }}</p>
        </div>
      </div>
    </div>
  </div>

  <app-button [buttonText]="'Edit'" (click)="handleEditClick()"></app-button>
  <app-button
    [buttonText]="'Cancel'"
    (click)="handleDeleteClick()"
  ></app-button>

  <form *ngIf="editMode">
    <label for="title">Title</label>
    <input type="text" [(ngModel)]="post.title" name="title" />
    <label for="content">Content</label>
    <input type="content" [(ngModel)]="post.content" name="email" />
    <label for="creationDate">Creation Date</label>
    <input type="date" [(ngModel)]="post.creationDate" name="creationDate" />
    <label for="isPublished">Publication State</label>
    <select [(ngModel)]="post.isPublished" name="isPublished">
      <option [value]="true">Active</option>
      <option [value]="false">Inactive</option>
    </select>
    <label for="user">User</label>
    <select [(ngModel)]="post.userId" name="user">
      <option *ngFor="let user of users" [value]="user.userId">
        {{ user.username }}
      </option>
    </select>
    <div>
      <app-button
        [buttonText]="'Save'"
        (click)="handleSaveClick()"
      ></app-button>
    </div>
  </form>
</div>
