import { Component } from '@angular/core';
import { Category } from '../category';
import { CategoryService } from '../category.service';
import { PostService } from 'src/app/posts/post.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-category-detail',
  templateUrl: './category-detail.component.html',
  styleUrls: ['./category-detail.component.css'],
})
export class CategoryDetailComponent {
  category: Category = {
    categoryId: 0,
    name: '',
    creationDate: '',
  };

  updatedCategory: Category = {
    categoryId: this.category.categoryId,
    name: '',
    creationDate: '',
  };

  postCount: number = 0;
  editMode: boolean = false;

  constructor(
    private categoryService: CategoryService,
    private postService: PostService,
    private activatedRoute: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit() {
    this.activatedRoute.params.subscribe((params) => {
      const id = params['id'];
      this.category = this.categoryService.getCategoryById(Number(id))!;
      if (this.postService.getPosts().length === 0) {
        this.postService.setPosts();
      }
      this.postCount = this.postService
        .getPosts()
        .filter((post) => post.categoryId === Number(id)).length;
    });
  }

  handleEditClick() {
    this.editMode = !this.editMode;
  }

  handleSaveClick() {
    this.categoryService.updatedCategory(this.updatedCategory);
    this.router.navigateByUrl('/categorylist');
  }

  handleCancelClick() {
    this.editMode = false;
    this.updatedCategory.creationDate = '';
    this.updatedCategory.name = '';
  }
}
<div class="category-detail-component">
  <div class="header">
    <p>Category Id: {{ category.categoryId }}</p>
    <h1>{{ category.name | uppercase }}</h1>
  </div>
  <div class="content">
    <h2>Creation Date</h2>
    <h2>{{ category.creationDate }}</h2>
  </div>
  <div class="footer">
    <div>
      <p>The {{ category.name }} category contains {{ postCount }} posts.</p>
    </div>
  </div>

  <app-button [buttonText]="'Edit'" (click)="handleEditClick()"></app-button>
  <app-button
    [buttonText]="'Return'"
    [routerLink]="'/categorylist'"
  ></app-button>

  <form *ngIf="editMode">
    <label for="name">Name</label> <br />
    <input type="text" [(ngModel)]="updatedCategory.name" name="name" /> <br />
    <label for="creationDate">Creation Date</label> <br />
    <input
      type="date"
      [(ngModel)]="updatedCategory.creationDate"
      name="creationDate"
    />
    <div>
      <app-button
        [buttonText]="'Save'"
        (onButtonClick)="handleSaveClick()"
      ></app-button>
      <app-button
        [buttonText]="'Cancel'"
        (onButtonClick)="handleCancelClick()"
      ></app-button>
    </div>
  </form>
</div>
